import { useEffect, useState } from "react";
import "./Accordion.css";
import MYCONSTANT from "../../constant/constant"

export default function Accordion(props) {
    const [tags, setTags] = useState([]);
    const [paths, setPaths] = useState([]);
    const [tagSelected, setTagSelected] = useState(null);
    const [pathSelected, setPathSelected] = useState(null);
    const [flag, setFlag] = useState(true);

   async function modifyResponse(d){
    
    if(d){
        let pathArr = [] ;
        await Object.keys(d).map(key => pathArr.push({
            objId:key,
            data:d[key]
        }));
        setPaths(pathArr)
        console.log("pathArr ", d);
    }
      
   }

    useEffect(() => {
        setTags(props.tags);
        
        modifyResponse(props.paths);

        // setPaths(props.paths);
      }, [props]);


      const tagToggle =(i)=>{
          if(tagSelected === i){
              return setTagSelected(null);
          }
          setTagSelected(i);
      }

      const pathToggle =(i)=>{
          if(pathSelected === i){
              return setPathSelected(null);
          }
          setPathSelected(i);
      }

      const getFlag = ()=> {
        setFlag(!flag);
      }

// console.log("acc tags", tags);
console.log("acc paths", paths);
  return (
    <div className="accordion-wrapper">
        <div className="accordion">
            {tags?.map((tag,i)=>
                <div className="item">
                    <div className="flex py-2 text-black border-b-2" onClick={()=>tagToggle(i)}>
                        <h2 className="font-bold text-2xl">{tag.name} &nbsp;
                            <small className="font-normal text-base">{tag.description}</small>
                        </h2>
                        <a href={tag?.externalDocs?.url} target="_blank" rel="noreferrer">{tag?.externalDocs?.description}</a>
                        <span>{tagSelected ===i?"-":"+"}</span>
                    </div>
                    <div className={tagSelected ===i?"content show":"content hide"}>
                    {/* ********************************************************** */}
                    
                        {/* {path?.map((path, i)=>)} */}
                         <div className="flex p-2 bg-green-200 border-green-300 border mt-2 text-black" onClick={()=>pathToggle(i)}>
                            <div>
                                <span className=" bg-green-400 border-green-500 border font-bold text-white py-1 px-4 rounded-sm">POST</span>
                                <strong className="p-4">/pet</strong>
                                <span>Add a new pet to the store</span>
                            </div>
                            <span>{pathSelected ===i?"-":"+"}</span>
                        </div>
                        <div className={pathSelected ===i?"content show bg-green-200 border-green-300":"content hide bg-green-200 border-green-300"}>
                            <div className="flex bg-white text-black p-4">
                                <div>Parameters</div>
                                <div className="border-black border-2 p-1 rounded-sm" onClick={(flag)=>getFlag()}>{flag?MYCONSTANT.TRY:MYCONSTANT.CANCEL}</div>
                            </div>
                        </div>
                        
                        
                         {/* ********************************************************** */}
                       
                       
                        {/* {paths?.map((path, i)=>
                        
                            
                        

                        <div className="item">
                        <div className="title" onClick={()=>togglePath(i)}>
                            <h2 className="font-bold">{path.name}</h2>
                            <div>{path.description}</div>
                            <a href={path?.externalDocs?.url} target="_blank">{path?.externalDocs?.description}</a>
                            <span>{selected ===i?"-":"+"}</span>
                        </div>
                        <div className={selected ===i?"content show":"content hide"}>

                        </div>
                    </div>




                        )} */}
                    </div>
                </div>
            )}
        </div>
      
    </div>
  );
}
