import React, { useEffect, useState } from "react";

function Info(props) {
    const [description, setDescription] = useState("");
    const [info, setInfo] = useState("");
    let parse = new DOMParser();

    const modifyResponse = (description) => {
        let getUrl = /(https?:\/\/[^\s]+)/g;

        setDescription(parse.parseFromString(description.replace(getUrl,
            function (url) {
                return "<a href='" + url + "' >" + url + "</a>";
            }),
            "text/html").body
            .innerHTML);
    }

    useEffect(() => {
        if (props.infoData) {
            setInfo(props.infoData);
            console.log(props.infoData);
            modifyResponse(props.infoData.description);
        }
    }, [props.infoData]);

    return <div className="Info">
        <h1 className="text-4xl mb-5 font-bold">{info.title}&nbsp;
        <small><sup className="text-white bg-slate-600 rounded-lg">
            {info.version}</sup></small></h1>
        <div dangerouslySetInnerHTML={{__html:description}}></div>
        <div className="LinkSection mt-5">
            <p><a href={info.contact.email}>{info.contact.email}</a></p>
            <p><a href={info.license.url}>{info.license.name}</a></p>
            <p><a href={info.termsOfService}>{info.termsOfService}</a></p>
        </div>
    </div>;
}

export default Info;
