import React, { useEffect, useState } from "react";

function Info(props) {
    const [description, setDescription] = useState("");
    const [info, setInfo] = useState("");
    let parse = new DOMParser();
    let modifiedResp = "";
    const modifyResponse = () => {
        let getUrl = /(https?:\/\/[^\s]+)/g;

        setDescription(parse.parseFromString(props.infoData.description.replace(getUrl,
            function (url) {
                return "<a href='" + url + "' class='text-blue-600' >" + url + "</a>";
            }),
            "text/html").body
            .innerHTML);
    }

    useEffect(() => {
        if (props.infoData) {
            console.log(props.infoData);
            setInfo(props.infoData);
            modifyResponse();
        }
    }, [props.infoData]);

    return <div className="Info">
        <h1 className="text-4xl mb-5 font-bold">{info.title} 
        <small><sup className="text-white bg-slate-600 rounded-lg">
            {info.version}</sup></small></h1>
            
        <div dangerouslySetInnerHTML={{__html:description}}></div>

    </div>;
}

export default Info;
