import React, { useEffect, useState } from "react";
import myConstant from "../constant/constant"

function Info(props) {
    const [description, setDescription] = useState("");
    const [info, setInfo] = useState("");
    let parse = new DOMParser();

    const modifyResponse = (description) => {
        let tagText = parse.parseFromString(description.replace(myConstant.GET_URL,
            function (url) {
                return "<a href='" + url + "' >" + url + "</a>";
            }),
            "text/html").body
            .innerHTML;

            setDescription(tagText.replace(myConstant.GET_TEXT, function (text) {
                return "<span class='text-purple-700 bg-slate-300'>" + text + "</span>";
            }));
    }

    useEffect(() => {
        if (props.infoData) {
            setInfo(props?.infoData);

            modifyResponse(props.infoData.description);
        }
    }, [props?.infoData]);

    return <div className="Info">
        <h1 className="text-4xl mb-5 font-bold">{info.title}&nbsp;
        <small><sup className="text-white rounded-lg bg-zinc-500">
            {info.version}</sup></small></h1>
        <div dangerouslySetInnerHTML={{__html:description}}></div>
        <div className="LinkSection mt-5">
            <p><a href={info.contact?.email}>{myConstant.CONTACT}</a></p>
            <p><a href={info.license?.url}>{info.license?.name}</a></p>
            <p><a href={info?.termsOfService}>{myConstant.TERMS}</a></p>
        </div>
    </div>;
}

export default Info;
