import React, { useEffect, useState } from "react";

function Info(props) {
    const [description, setDescription] = useState(<></>);
    const [info, setInfo] = useState("");
    let parse = new DOMParser();

    const modifyResponse = () =>{
         let getUrl = /(https?:\/\/[^\s]+)/g;
         
         let ddd = parse.parseFromString(props.infoData.description.replace(getUrl, 
            function(url){
                return '<a href="'+url+'" >'+url+'</a>';
            }),
             "text/html").body
            .innerHTML;

          
    }

  useEffect(() => {
    if (props.infoData) {
        console.log(props.infoData);
        setInfo(props.infoData);
        modifyResponse();
    //   setDescription(props.infoData.description);
    }
  }, [props.infoData]);

  return (<div className="Info">
      <h1 className="text-4xl mb-5 font-bold">{info.title} <small><sup className="text-white bg-slate-600 rounded-lg">{info.version}</sup></small></h1>
       <p className="">{description}</p>
      
      </div>);
}

export default Info;
