import { useEffect, useState } from "react";
import "./Accordion.css";
import MYCONSTANT from "../../constant/constant";
import Paths from "../Paths";
export default function Accordion(props) {
  const [tag, setTag] = useState([]);
  const [paths, setPaths] = useState([]);
  const [tagSelected, setTagSelected] = useState(null);
  const [pathSelected, setPathSelected] = useState(null);
  const [flag, setFlag] = useState(true);
  // const [pathsArr, setPathsArr] = useState([]);
  // const [methodArr, setMethodArr] = useState([]);

  useEffect(() => {
    // if (props?.paths) {
    //   setPathsArr(
    //     Object.keys(props?.paths).map((key) => [Number(key), props?.paths[key]])
    //   );

    // }
    setTag(props.tags);
    setPaths(props?.paths);
  }, [props]);

  const tagToggle = (i) => {
    if (tagSelected === i) {
      return setTagSelected(null);
    }
    setTagSelected(i);
  };

  const pathToggle = (i) => {
    if (pathSelected === i) {
      return setPathSelected(null);
    }
    setPathSelected(i);
  };

  const getFlag = () => {
    setFlag(!flag);
  };

  // console.log("keysName ", pathsArr);
  console.log("acc paths", paths);
  return (
    <div className="accordion-wrapper">
      <div className="accordion">
        {tag?.map((t, i) => (
          <div className="item">
            <div
              className="flex py-2 text-black border-b-2"
              onClick={() => tagToggle(i)}
            >
              <h2 className="font-bold text-2xl">
                {t.name} &nbsp;
                <small className="font-normal text-base">{t.description}</small>
              </h2>
              <a href={t?.externalDocs?.url} target="_blank" rel="noreferrer">
                {t?.externalDocs?.description}
              </a>
              <span>{tagSelected === i ? "-" : "+"}</span>
            </div>
            <div
              className={tagSelected === i ? "content show" : "content hide"}
            >
              
              <Paths />
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
