import { useEffect, useState } from "react";

function Info(props) {
    const [description, setDescription] = useState("");
    const [info, setInfo] = useState("");
  //  let regx = /(https?:\/\/[^\s]+)/g;

  useEffect(() => {
    if (props.infoData) {
        console.log(props.infoData);
        setInfo(props.infoData.title);
      setDescription(props.infoData.description);
    }
  }, [props.infoData]);

  return (<div className="Info"> {description}
      
      </div>);
}

export default Info;
