import { useEffect, useState } from "react";

function Info(props) {
  const [description, setDescription] = useState("");

  //  let regx = /(https?:\/\/[^\s]+)/g;

  useEffect(() => {
    if (props.infoData) {
      setDescription(props.infoData.description);
    }
  }, props);

  return <div className="Info">{description}</div>;
}

export default Info;
