import { useEffect, useState } from "react";
import "./Accordion.css";
import MYCONSTANT from "../../constant/constant";

export default function Accordion(props) {
  const [tags, setTags] = useState([]);
  const [paths, setPaths] = useState([]);
  const [tagSelected, setTagSelected] = useState(null);
  const [pathSelected, setPathSelected] = useState(null);
  const [flag, setFlag] = useState(true);
  const [pathsArr, setPathsArr] = useState([]);
  const [method, setMethod] =useState([]);

  useEffect(() => {
    if (props?.paths) {
      setPathsArr(Object.keys(props?.paths).map((key) => [Number(key), props?.paths[key]]));
    }
    setTags(props.tags);
    setPaths(props.paths);
  }, [props]);

  const tagToggle = (i) => {
    if (tagSelected === i) {
      return setTagSelected(null);
    }
    setTagSelected(i);
  };

  const pathToggle = (i) => {
    if (pathSelected === i) {
      return setPathSelected(null);
    }
    setPathSelected(i);
  };

  const getFlag = () => {
    setFlag(!flag);
  };

  console.log("keysName ", pathsArr);
  console.log("acc paths", paths);
  return (
    <div className="accordion-wrapper">
      <div className="accordion">
        {tags?.map((tag, i) => (
          <div className="item">
            <div
              className="flex py-2 text-black border-b-2"
              onClick={() => tagToggle(i)}
            >
              <h2 className="font-bold text-2xl">
                {tag.name} &nbsp;
                <small className="font-normal text-base">
                  {tag.description}
                </small>
              </h2>
              <a href={tag?.externalDocs?.url} target="_blank" rel="noreferrer">
                {tag?.externalDocs?.description}
              </a>
              <span>{tagSelected === i ? "-" : "+"}</span>
            </div>
            <div
              className={tagSelected === i ? "content show" : "content hide"}
            >
              {/* ********************************************************** */}

              {pathsArr.map((data, i) => (
                data[i]?
                 Object.keys(data[i]).map((method,i)=>

                <div
                  className={
                    method === MYCONSTANT.POST
                      ? "border-green-300 border my-2"
                      : method === MYCONSTANT.PUT
                      ? "border-orange-300 border my-2"
                      : method === MYCONSTANT.GET
                      ? "border-sky-300 border my-2"
                      : "border-red-300 border my-2"
                  }
                >
                  <div
                    className={
                      method === MYCONSTANT.POST
                        ? "flex p-2 bg-green-200 border-green-300 border text-black"
                        :method === MYCONSTANT.PUT
                        ? "flex p-2 bg-orange-200 border-orange-300 border text-black"
                        : method === MYCONSTANT.GET
                        ? "flex p-2 bg-sky-200 border-sky-300 border text-black"
                        : "flex p-2 bg-red-200 border-red-300 border text-black"
                    }
                    onClick={() => pathToggle(i)}
                  >
                    <div>
                      <span
                        className={
                          method === MYCONSTANT.POST
                            ? "bg-green-400 border-green-500 border font-bold text-white py-1 px-4 rounded-sm uppercase"
                            : method === MYCONSTANT.PUT
                            ? "bg-orange-400 border-orange-500 border font-bold text-white py-1 px-4 rounded-sm uppercase"
                            : method === MYCONSTANT.GET
                            ? "bg-sky-400 border-sky-500 border font-bold text-white py-1 px-4 rounded-sm uppercase"
                            : "bg-red-400 border-red-500 border font-bold text-white py-1 px-4 rounded-sm uppercase"
                        }
                      >
                        {paths["/" + tag[i]?.name]?.method !== null
                          ? method
                          : null}
                      </span>
                      <strong className="p-4">{"/" + tags[i]?.name}</strong>
                      <span>{paths["/" + tags[i]?.name]?.method?.summary}</span>
                    </div>
                    <span>{pathSelected === i ? "-" : "+"}</span>
                  </div>
                  <div
                    className={
                      pathSelected === i
                        ? method === MYCONSTANT.POST
                          ? "content show bg-green-200 border-green-300"
                          : method === MYCONSTANT.PUT
                          ? "content show bg-orange-200 border-orange-300"
                          : method === MYCONSTANT.GET
                          ? "content show bg-sky-200 border-sky-300"
                          : "content show bg-red-200 border-red-300"
                        : method === MYCONSTANT.POST
                        ? "content hide bg-green-200 border-green-300"
                        : method === MYCONSTANT.PUT
                        ? "content hide bg-orange-200 border-orange-300"
                        : method === MYCONSTANT.GET
                        ? "content hide bg-sky-200 border-sky-300"
                        : "content hide bg-red-200 border-red-300"
                    }
                  >
                    <div className="flex bg-white text-black p-4 shadow-md">
                      <div>{MYCONSTANT.PARAMETERS}</div>
                      <div
                        className="border-black border-2 p-1 rounded-sm"
                        onClick={(flag) => getFlag()}
                      >
                        {flag ? MYCONSTANT.TRY : MYCONSTANT.CANCEL}
                      </div>
                    </div>
                    <div className="text-black px-2">
                      <table class="table-fixed">
                        <thead>
                          <tr>
                            <th>{MYCONSTANT.NAME}</th>
                            <th>{MYCONSTANT.DESCRIPTION}</th>
                          </tr>
                        </thead>
                        <tbody>
                          <tr>
                            <td>
                              <p>
                                {
                                  paths["/" + tags[i]?.name]?.post
                                    ?.parameters[0]?.name
                                }
                                <small className="text-red-900">
                                  <sup>{MYCONSTANT.REQUIRED}</sup>
                                </small>
                              </p>
                              &nbsp;
                            </td>
                            <td>
                              {
                                paths["/" + tags[i]?.name]?.post?.parameters[0]
                                  .description
                              }
                            </td>
                          </tr>
                        </tbody>
                      </table>
                    </div>
                  </div>
                </div>
                )
              :null))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
